mapper.select:
select
*
from train_num
where start_station = #{station,jdbcType=VARCHAR}
limit #{start},#{limit}

mapper.update:
update train_num
set start_station = #{startStation,jdbcType=VARCHAR},
  des_station = #{desStation,jdbcType=VARCHAR},
  go_off = #{goOff,jdbcType=TIMESTAMP},
  arrive = #{arrive,jdbcType=TIMESTAMP}
where train_no = #{trainNo,jdbcType=VARCHAR}
