  #{idCardType,jdbcType=VARCHAR}, #{idCard,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}, 
  #{phoneNumber,jdbcType=VARCHAR}, #{log.lId,jdbcType=BIGINT}, #{userType,jdbcType=CHAR}
