mapper.select:
select
*,count(*) as count
from train_ticket tt join train_num tn on tt.train_no = tn.train_no
    join train_seat ts on ts.tt_id = tt.tt_id
where tt.start_place = #{startPlace,jdbcType=VARCHAR}
and tt.des_place = #{desPlace,jdbcType=VARCHAR}
and ts.seat_state = false
and tt.go_off between #{goOff} and  #{endTime}
group by ts.seat_type
order by tt.go_off asc

select
*
from train_ticket
where train_no = #{trainNo,jdbcType=VARCHAR}
and start_station = #{startStation,jdbcType=VARCHAR}

select
*
from train_ticket
where train_no = #{trainNo,jdbcType=VARCHAR}
and des_station = #{desStation,jdbcType=VARCHAR}
